import React from 'react';
import './ProductCard.css';

const ProductCard = ({ name, description, image }) => {
  return (
    <div className="card">
      <img src={image} alt={name} className="card-img" />
      <div className="card-content">
        <h2>{name}</h2>
        <p>{description}</p>
      </div>
    </div>
  );
};

export default ProductCard;
