import React from 'react';
import ProductCard from './ProductCard';
import './ProductList.css';
import EldenRingCover from '../assets/images/EldenRingCover.jpeg';
import FinalFantasy16Cover from '../assets/images/FFXVICover.jpg';
import PokemonScarletCover from '../assets/images/PokemonCover.jpg';
import DemonSoulsCover from '../assets/images/DemonSoulsCover.jpeg';
import GTAVCover from '../assets/images/GTAVCover.jpeg';
import FC24Cover from '../assets/images/FC24.jpeg';
import Madden25Cover from '../assets/images/Madden25.jpeg';
import Civilization6Cover from '../assets/images/Civ6Cover.jpg';

const games = [
  {
    id: 1,
    name: 'Elden Ring',
    image: EldenRingCover,
    price: 69.99,
    rating: 5,
    description: 'Elden Ring is an action RPG which takes place in the Lands Between, sometime after the Shattering of the titular Elden Ring. Players must explore and fight their way through the vast open-world to unite all the shards, restore the Elden Ring, and become Elden Lord.'
  },
  {
    id: 2,
    name: 'Final Fantasy 16',
    image: FinalFantasy16Cover,
    price: 69.99,
    rating: 4,
    description: 'The latest entry in the Final Fantasy series, featuring stunning visuals and epic storytelling.'
  },
  {
    id: 3,
    name: 'Pokemon Scarlet',
    image: PokemonScarletCover,
    price: 59.99,
    rating: 4,
    description: 'A new adventure in the Pokémon world, filled with new creatures to catch and battle.'
  },
  {
    id: 4,
    name: 'Demon Souls',
    image: DemonSoulsCover,
    price: 59.99,
    rating: 5,
    description: 'A remake of the classic action RPG, known for its punishing difficulty and dark atmosphere.'
  },
  {
    id: 5,
    name: 'GTA V',
    image: GTAVCover,
    price: 29.99,
    rating: 4,
    description: 'A sprawling open-world game set in Los Santos, offering endless opportunities for chaos and fun.'
  },
  {
    id: 6,
    name: 'FC 24',
    image: FC24Cover,
    price: 79.99,
    rating: 3,
    description: 'The latest in the football simulation series, with improved gameplay and new features.'
  },
  {
    id: 7,
    name: 'Madden 25',
    image: Madden25Cover,
    price: 79.99,
    rating: 3,
    description: 'Celebrate 25 years of Madden NFL with enhanced graphics, new moves, and legendary players.'
  },
  {
    id: 8,
    name: 'Civilization 6',
    image: Civilization6Cover,
    price: 39.99,
    rating: 4,
    description: 'A strategy game where you build an empire that stands the test of time, now with new civilizations and scenarios.'
  }
];

function ProductList({ searchTerm }) {
  const filteredGames = games.filter(game =>
    game.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="product-list">
      {filteredGames.map(game => (
        <ProductCard
          key={game.id}
          name={game.name}
          image={game.image}
          price={game.price}
          rating={game.rating}
          description={game.description}
        />
      ))}
    </div>
  );
}

export default ProductList;
